const startFaceLogin = async () => {
  // 1. Start SDK Face Login (Skip tutorials)
  const result = await IncodeSdk.startFaceLogin({ showTutorials: false });

  // 2. Securely fetch OCR data via Backend API
  const { ocrData } = await berifyAppApi.incode.getOcrData({
    id: result.customerUUID,
    token: result.token
  });

  // 3. Call verify-idv API for Server-Side 4-Way Matching
  const response = await berifyAppApi.user.verifyIDV(userId, {
    firstName: ocrData.name.firstName,
    lastName: ocrData.name.paternalLastName,
    email: session.user.email,
    idvToken: result.token
  });

  if (response.success) {
    // 4. Success: Log time and grant access
    await AsyncStorage.setItem('idvVerifyTime', JSON.stringify(new Date()));
    setIsBerifyToolEnable(true);
    navigation.navigate('Landing');
  } else {
    // 5. Mismatch: System emails the Inviter (Manager) for resolution
    Alert.alert('Notice', response.error);
  }
};

const handleScan = async () => {
  setScanning(true);
  try {
    await NfcManager.requestTechnology(...);
    // ... processing logic
  } catch (e) {
    navigation.navigate('ErrorScreen', { error: e });
  } finally {
    setScanning(false); // Atomic reset
    NfcManager.cancelTechnologyRequest();
  }
};

const claimNft = async () => {
  setUiLocked(true);
  setClaimStatus('pending');
  try {
    await blockchainService.claim(nftId);
    setClaimStatus('success');
  } catch {
    setClaimStatus('failed');
    revertOptimisticChanges();
  } finally {
    setUiLocked(false);
  }
};

