export const videoToAutoQuality = async (videoSrc: string) => {
  const hqUrl = videoSrc.replace(/\.[^/.]+$/, "_hq.mp4");
  // Probing HQ resource via HEAD request; fallback to original on failure
  return fetch(hqUrl, { method: 'HEAD' })
    .then(res => res.status === 200 ? hqUrl : videoSrc)
    .catch(() => videoSrc);
};

// 100% Implemented by Chester: Error-code driven routing
const onRedirectScanResult = (error?: string) => {
  switch (error) {
    case 'Token is not exist.':
    case 'tokenId and UID do not match.':
      navigation.navigate(routes.scanResult.verifyError);
      break;
    case 'This tag is deactivated.':
      navigation.navigate(routes.scanResult.inactive);
      break;
    default:
      if (!error) {
        // Success Path: Delayed navigation for visual continuity
        setTimeout(() => navigation.navigate(routes.scanResult.success), DELAY_MS);
      } else {
        navigation.navigate(routes.scanResult.timeOut);
      }
  }
};
