// Ensure atomicity of App Clip scan flow via recursive dismiss
func useLandingController() -> LandingViewController? {
  let keyWindow = UIApplication.shared.windows.filter {$0.isKeyWindow}.first
  if var topController = keyWindow?.rootViewController {
    // Find the topmost View Controller
    while let presentedViewController = topController.presentedViewController {
      topController = presentedViewController
    }
    // Safe cleanup: Dismiss only scan-related pages
    if topController is ScanResultViewController || topController is ScanErrorViewController {
        topController.dismiss(animated: false)
    }
  }
  return keyWindow?.rootViewController as? LandingViewController
}
