const handleScan = async () => {
  setScanning(true);
  try {
    await NfcManager.requestTechnology(...);
    // ... processing logic
  } catch (e) {
    navigation.navigate('ErrorScreen', { error: e });
  } finally {
    setScanning(false); // Atomic reset
    NfcManager.cancelTechnologyRequest();
  }
};

const claimNft = async () => {
  setUiLocked(true);
  setClaimStatus('pending');
  try {
    await blockchainService.claim(nftId);
    setClaimStatus('success');
  } catch {
    setClaimStatus('failed');
    revertOptimisticChanges();
  } finally {
    setUiLocked(false);
  }
};

// Next.js API Route (IDOR Prevention)
export async function PATCH(req: Request) {
  const session = await getServerSession();
  const userId = session.user.id; // From token, NOT body

  return prisma.userProfile.upsert({
    where: { userId }, // Auth-derived ID
    update: { idvStatus: 'verified' },
    create: { userId, idvStatus: 'pending' },
  });
}
