// State guard logic
if (!player.playing) {
  // Only reset list when not playing
  resetPlaylist(filteredSongs);
} else {
  // Update view only when playing
  updateViewOnly(filteredSongs);
}

// Version migration logic
const currentVersion = await db.getVersion();
if (currentVersion < PRAGMA user_version) {
  await runMigrations(currentVersion);
  await db.setVersion(PRAGMA user_version);
}

// Resumable download implementation
const downloadResumable = FileSystem
  .createDownloadResumable(
    uri, fileUri,
    {}, // options
    (progress) => updateProgress(progress)
  );

await downloadResumable.downloadAsync();
