// 透過遞迴 dismiss 確保每次掃描都能從初始的 Landing 狀態啟動
func useLandingController() -> LandingViewController? {
  let keyWindow = UIApplication.shared.windows.filter {
    $0.isKeyWindow
  }.first

  if var topController = keyWindow?.rootViewController {
    while let presented = 
      topController.presentedViewController {
      topController = presented
    }

    if topController is ScanResultViewController ||
       topController is ScanErrorViewController {
        topController.dismiss(animated: false)
    }
  }
  return keyWindow?.rootViewController as? LandingViewController
}

// 1. 在 Context 中定義原生動畫插值 (Interpolation)
const styles = useMemo(() => ({
  background: {
    transform: [
      {
        translateY: scrollY.interpolate({
          inputRange: [0, headerHeight],
          outputRange: [0, -headerHeight]
        })
      },
      {
        scale: scrollY.interpolate({
          inputRange: [-windowHeight, 0],
          outputRange: [4.2, 1]
        })
      }
    ]
  }
}), [scrollY, headerHeight]);

const onScroll = Animated.event(
  [{ nativeEvent: { contentOffset: { y: scrollY } } }],
  { useNativeDriver: true }
);

// 2. 在 UI 層消費 Context 並綁定動畫
function CustomFlatList({ children, BackgroundComponent }) {
  const { styles, onScroll } = useCustomFlatListContext();
  return (
    <Stack>
      <Animated.View style={[styles.background]} pointerEvents='box-none'>
        {BackgroundComponent}
      </Animated.View>
      <Animated.FlatList
        onScroll={onScroll}
        scrollEventThrottle={16}
        {...props}
      />
    </Stack>
  );
}
